def jsonsql_call(method, params: dict, request_id=1, uri=RPC_URI, session_id=SESSION_ID, timeout=360):
    request_dict = {
        "jsonrpc": "2.0",
        "id": request_id,
        "method": method,
        "params": params
    }
    try:
        headers = {'Iptvportal-authorization': f'sessionid={session_id}',
                   'Accept': 'application/json'} if session_id else None
        logger.info(f'URI -- {uri}, SESS_ID -- {session_id}')

        resp = requests.request('POST', uri, data=orjson.dumps(request_dict), headers=headers, timeout=timeout,
                                verify=False)
        if resp.status_code == 200:
            result: list[list] or dict = orjson.loads(resp.content)
            return result.get('result', result)
        if resp.status_code == 401:
            logger.warning('REAUTH')
            s: dict = jsonsql_call('authorize_user', params=dict(username=USERNAME, password=PASSWORD), uri=AUTH_URI,
                                   session_id=None)
            session_id = s['session_id']
            result: list or dict = jsonsql_call(method, params, session_id=session_id)
            return result
        if resp.status_code in range(500 - 510):
            logger.critical(resp.text)
            return resp.text

    except rexc.ReadTimeout or rexc.Timeout or rexc.ConnectionError or rexc.HTTPError as rto:
        logger.exception(rto)
        return


