def prepare_playlog(raw_log):
    log_list = []
    for log in raw_log:
        playlog_start = dt.strptime(log[0], "%Y-%m-%d %H:%M:%S")
        d = {}
        d['date'] = playlog_start.date()
        d['dow'] = playlog_start.isoweekday()
        d['hod'] = playlog_start.hour
        d['playlog_start'] = playlog_start
        d['terminal_domain_id'] = int(log[1])
        d['terminal_mac_addr'] = str(log[2])
        d['tv_channel_id'] = int(log[3])
        d['tv_channel_name'] = str(log[4])
        # d['tv_channel_name_cleared'] = None
        d['epg_program_id'] = log[5]
        d['epg_program_title'] = log[6]
        d['epg_program_category_id'] = log[7]
        d['epg_program_category_name'] = log[8]
        d['epg_program_category_genre'] = log[9]
        d['epg_program_start'] = dt.strptime(log[10], "%Y-%m-%d %H:%M:%S")
        d['epg_program_stop'] = dt.strptime(log[11], "%Y-%m-%d %H:%M:%S")
        log_list.append(d)
    query = (F"INSERT INTO playlogs ({','.join(log_list[0].keys())}) VALUES", log_list)
    # print(pjd(log_list))
    return query



